mapper.select:
SELECT *
FROM MEMBER
WHERE member_id = #{member_id}
AND member_pw = #{member_pw}

mapper.insert:
INSERT INTO member (
   member_id, member_pw, member_nick,
   member_name, member_since, member_check,
   member_smscheck, member_type
)
VALUES (
   #{member_id}, #{member_pw}, #{member_nick},
   #{member_name}, now(), #{member_check},
   #{member_smscheck}, #{member_type}
)
